@OptIn(ExperimentalGlideComposeApi::class)
@Composable
fun GameRow(game:Game) {
    Row {

//        Image(
//            painter = painterResource(id = R.drawable.ic_launcher_background),
//            contentDescription = game.name
//        )
        GlideImage(model =game.thumbnail ,
            contentDescription = game.title,
            loading = placeholder(R.drawable.ic_launcher_1_background),
            modifier= Modifier.width(100.dp).height(100.dp).padding(8.dp)
        )
        Column {
            Text(text = game.title)
            Text(text = game.developer, fontSize = 8.sp)
        }
    }
}

